// frontend/src/App.js
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useAuth } from './compo';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from '../src/components/AuthContext';

// Importar tus páginas o componentes aquí
import Login from './components/Login'; //
import UserPage from './components/UserPage';
import AdminPage from './components/AdminPage';
import ProviderPage from './components/ProviderPage';
import SupportPage from './components/SupportPage';

function App() {
  const { authState } = useAuth();
  return (
    <AuthProvider>
    <Router>
      <Routes>
      <Route path="/Login" element={<Login />} />
      <Route path="/pagina-usuario" element={authState.NombreUsuario ? <UserPage /> : <Navigate to="/login" />} />
      <Route path="/pagina-administrador" element={authState.NombreUsuario ? <AdminPage /> : <Navigate to="/login" />} />
      <Route path="//pagina-proveedor" element={authState.NombreUsuario ? <ProviderPage /> : <Navigate to="/login" />} />
      <Route path="/pagina-soporte" element={authState.NombreUsuario ? <SupportPage /> : <Navigate to="/login" />} />
        {/* Puedes agregar más rutas según sea necesario */}
      </Routes>
    </Router>
    </AuthProvider>
  );
}
export default App;
