import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button, Modal, FormControl, InputGroup, Image } from 'react-bootstrap';

const getImageUrl = (imageName) => {
  return `http://localhost:5000/${imageName}`;
};

const Productos = ({ changeView }) => {
  const [productos, setProductos] = useState([]);
  const [modalShow, setModalShow] = useState(false);
  const [productoActivo, setProductoActivo] = useState({});
  const [filtro, setFiltro] = useState('');

  useEffect(() => {
    fetchProductos();
  }, []);

  const fetchProductos = () => {
    axios.get('http://localhost:5000/api/ObtenerProductos/')
      .then(response => {
        const productosAprobados = response.data.filter(producto => producto.Aprobado === 1);
        setProductos(productosAprobados);
      })
      .catch(err => {
        console.error("Error al cargar los productos:", err);
      });
  };

  // Esta función ahora manejará agregar al carrito y mostrar los detalles
  const agregarYMostrarDetalles = (producto) => {
    // Esta función ahora actualiza el carrito y muestra los detalles
    actualizarCarrito(producto, 1); // Incrementa la cantidad en 1
    handleShowDetails(producto);
  };

  // Esta función actualiza el carrito tanto en el estado como en el localStorage
  const actualizarCarrito = (producto, cantidad) => {
    let carrito = JSON.parse(localStorage.getItem('cartItems')) || [];
    const index = carrito.findIndex((item) => item.Id === producto.Id);

    if (index !== -1) {
      // Producto existe, actualizar la cantidad
      carrito[index].cantidad += cantidad;
    } else {
      // Producto nuevo, agregar al carrito con cantidad inicial
      carrito.push({ ...producto, cantidad: cantidad });
    }

    localStorage.setItem('cartItems', JSON.stringify(carrito));
    // Si necesitas actualizar el estado en Productos, puedes emitir un evento o usar Context/Redux
  };


  // Función para mostrar los detalles del producto
  const handleShowDetails = (producto) => {
    setProductoActivo(producto);
    setModalShow(true);
  };

  // Filtro para los productos basado en la entrada del usuario
  const productosFiltrados = productos.filter(producto =>
    producto.Nombre.toLowerCase().includes(filtro.toLowerCase())
  );

  return (
    <>
      <div className="container mt-5">
        <InputGroup className="mb-3">
          <FormControl
            placeholder="Buscar producto..."
            onChange={(e) => setFiltro(e.target.value)}
          />
        </InputGroup>
        <h2>Productos Disponibles</h2>
        <div className="row">
          {productosFiltrados.map((producto) => (
            <div key={producto.Id} className="col-md-3 mb-3">
              <Card>
                <Card.Img variant="top" src={getImageUrl(producto.Imagen)} className="img-cuadrada" alt={`Imagen de ${producto.Nombre}`} />
                <Card.Body>
                  <Card.Title>{producto.Nombre}</Card.Title>
                  <Card.Text>Precio: ${producto.Precio.toLocaleString()}</Card.Text>
                  <Button variant="primary" onClick={() => agregarYMostrarDetalles(producto)}>
                    Agregar al Carrito
                  </Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
      <ProductoModal
        show={modalShow}
        onHide={() => setModalShow(false)}
        producto={productoActivo}
        actualizarCarrito={actualizarCarrito}
        changeView={changeView}
      />
    </>
  );
};

const ProductoModal = ({ show, onHide, producto, actualizarCarrito, changeView }) => {
  //const [cantidad, setCantidad] = useState(1);

  useEffect(() => {
    if (show) {
      setCantidad(1);
    }
  }, [show]);

  
  const incrementarCantidad = () => {
    actualizarCarrito(producto.Id, 1);
  };

  const decrementarCantidad = () => {
    actualizarCarrito(producto.Id, -1);
  };

  const navigateToCart = () => {
    onHide();
    changeView('ShoppingCart');
  };

  // Obtenemos la cantidad actual del producto en el carrito
  const cantidadActual = JSON.parse(localStorage.getItem('cartItems'))?.find(item => item.Id === producto.Id)?.cantidad || 0;

  return (
    <Modal show={show} onHide={onHide} size="md" aria-labelledby="contained-modal-title-vcenter" centered>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          {producto?.Nombre || 'Cargando...'}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* ... rest of your existing JSX for Modal.Body ... */}
        </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Seguir comprando
        </Button>
        <Button variant="primary" onClick={navigateToCart}>
          Ir al Carro
        </Button>
      </Modal.Footer>
    </Modal>
  );
};
export default Productos;
