// EditUserForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function EditUserForm() {
    const { userId } = useParams();
    const [userData, setUserData] = useState({
        NombreUsuario: '',
        Apellido: '',
        Email: '',
        Contraseña: '',
        Documento_Numero: '',
        Celular_Numero: '',
        Direccion: '',
        Comuna: ''
    });
    const [currentPassword, setCurrentPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [repeatNewPassword, setRepeatNewPassword] = useState('');
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:5000/api/Users/${userId}`)
            .then(response => {
                setUserData(response.data);
            })
            .catch(error => {
                console.error(error);
                setError('Error al cargar los datos del usuario');
            });
    }, [userId]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:5000/api/EditUsers/${userId}`, userData);
            alert('Información actualizada exitosamente');
        } catch (error) {
            console.error('Error al actualizar la información:', error);
            setError('Error al actualizar la información');
        }
    };

    const handlePasswordChange = async (e) => {
        e.preventDefault();

        if (newPassword !== repeatNewPassword) {
            alert('Las contraseñas no coinciden.');
            return;
        }
        try {
            await axios.post(`http://localhost:5000/api/usuarios/cambio-contrasena`, {
                email: userData.Email,
                currentPassword,
                newPassword
            });
            alert('Contraseña actualizada exitosamente');
        } catch (error) {
            console.error('Error al cambiar la contraseña:', error);
            setError('Error al actualizar la contraseña');
        }
    };

    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                {/* Campos del formulario aquí */}
                <div className="mb-3">
                    <label htmlFor="NombreUsuario" className="form-label">Nombre de Usuario</label>
                    <input
                        type="text"
                        className="form-control"
                        id="NombreUsuario"
                        name="NombreUsuario"
                        value={userData.NombreUsuario}
                        onChange={handleChange}
                    />
                </div>

                <button type="submit" className="btn btn-primary">Actualizar</button>
            </form>
        </div>
    );
}

export default EditUserForm;
