// frontend/src/App.js
import React, { useContext, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { AuthProvider, AuthContext } from './components/AuthContext';

// Importar tus páginas o componentes aquí
import UserPage from './components/UserPage';
import AdminPage from './components/AdminPage';
import ProviderPage from './components/ProviderPage';
import SupportPage from './components/SupportPage';
import LoginPage from './components/LoginPage'; // Asegúrate de importar tu componente de inicio de sesión

function App() {
  return (
    <AuthProvider>
      <Router>
        <AuthenticationCheck>
          <Routes>
            <Route path="/pagina-usuario" element={<UserPage />} />
            <Route path="/pagina-administrador" element={<AdminPage />} />
            <Route path="/pagina-proveedor" element={<ProviderPage />} />
            <Route path="/pagina-soporte" element={<SupportPage />} />
            <Route path="/pagina-usuario" element={<LoginPage />} />
            {/* Puedes agregar más rutas según sea necesario */}
          </Routes>
        </AuthenticationCheck>
      </Router>
    </AuthProvider>
  );
}

function AuthenticationCheck({ children }) {
  const { usuario, login } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
    const tipoUsuario = localStorage.getItem('tipoUsuario');

    if (token && userId && tipoUsuario) {
      login({ userId, tipoUsuario, token });
    } else {
      navigate('/login');
    }
  }, [login, navigate]);

  return children;
}

export default App;
