// AuthProvider.js
import React, { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [usuario, setUsuario] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        const userId = localStorage.getItem('userId');
        const tipoUsuario = localStorage.getItem('tipoUsuario');
        
        if (token && userId && tipoUsuario) {
            setUsuario({
                userId,
                tipoUsuario: tipoUsuario,
                token
            });
        }
    }, []);

    const login = (datosUsuario) => {
        localStorage.setItem('token', datosUsuario.token);
        localStorage.setItem('userId', datosUsuario.userId);
        localStorage.setItem('tipoUsuario', datosUsuario.tipoUsuario); // Asegúrate de que esto se guarde
        
        setUsuario({
            userId: datosUsuario.userId,
            TipoUsuario: datosUsuario.tipoUsuario,
            token: datosUsuario.token
        });
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userId'); // Asegúrate de eliminar esto también
        localStorage.removeItem('tipoUsuario'); // Y esto
        setUsuario(null);
    };

    return (
        <AuthContext.Provider value={{ usuario, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
