import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Button, FormCheck, Alert } from 'react-bootstrap';

function UpProducto() {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [error, setError] = useState('');
  const [isApproving, setIsApproving] = useState(false);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get('http://localhost:5000/api/Aprobar/getPendingProducts')
      .then(response => setProducts(response.data))
      .catch(err => {
        console.error("Hubo un error al obtener los productos pendientes:", err);
        setError('Hubo un error al cargar los productos pendientes.');
      });
  };

  const handleApproval = (productId) => {
    setIsApproving(true);
    axios.patch(`http://localhost:5000/api/clients/updateProductApproval/${productId}`, { aprobado: 1 })
      .then(response => {
        console.log(response.data.message);
        fetchProducts(); // Recarga los productos pendientes
      })
      .catch(err => {
        console.error("Error durante la aprobación:", err);
        setError('Error durante la aprobación del producto.');
      })
      .finally(() => {
        setIsApproving(false);
      });
  };

  return (
    <div className="container my-5">
      <h2>Productos pendientes de aprobación</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Aprobar</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={product.id}>
              <td>{index + 1}</td>
              <td>{product.nombre}</td>
              <td>{product.descripcion}</td>
              <td>
                <Button 
                  variant="success" 
                  onClick={() => handleApproval(product.id)}
                  disabled={isApproving || selectedProducts.includes(product.id)}
                >
                  {isApproving && selectedProducts.includes(product.id) ? 'Aprobando...' : 'Aprobar'}
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default UpProducto;
