import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Mensajes() {
    const [mensajesEnviados, setMensajesEnviados] = useState([]);
    const [mensajesRecibidos, setMensajesRecibidos] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [nuevoMensaje, setNuevoMensaje] = useState('');

    useEffect(() => {
        // Obtener mensajes enviados
        axios.get('http://localhost:5000/api/mensajes/enviados/11')
            .then(response => {
                setMensajesEnviados(response.data);
            })
            .catch(error => console.error('Error al obtener mensajes enviados', error));

        // Obtener mensajes recibidos
        // Suponiendo que tienes una endpoint para esto, ajusta según tu API
        axios.get('http://localhost:5000/api/mensajes/recibidos')
            .then(response => {
                setMensajesRecibidos(response.data);
                setIsLoading(false);
            })
            .catch(error => {
                console.error('Error al obtener mensajes recibidos', error);
                setIsLoading(false);
            });
    }, []);

    const enviarMensaje = () => {
        // Enviar un nuevo mensaje
        // Ajusta los datos y la URL según tu API
        axios.post('http://localhost:5000/api/mensajes/crear', {
            emisorId: 11, 
            receptorId: 8,
            mensaje: nuevoMensaje,
            productoId: 9
        })
        .then(response => {
            // Actualiza la lista de mensajes o notifica al usuario
        })
        .catch(error => console.error('Error al enviar mensaje', error));
    };

    const marcarComoLeido = (idMensaje) => {
        // Marcar un mensaje como leído
        axios.put(`http://localhost:5000/api/mensajes/marcar-leido/${idMensaje}`)
            .then(response => {
                // Actualiza el estado o la UI según sea necesario
            })
            .catch(error => console.error('Error al marcar mensaje como leído', error));
    };

    return (
        <div>
            <h2>Mensajes</h2>
            {isLoading && <p>Cargando mensajes...</p>}
            {!isLoading && mensajes.length === 0 && <p>No tienes mensajes.</p>}
            {!isLoading && mensajes.length > 0 && (
                <ul>
                    {mensajes.map(mensaje => (
                        <li key={mensaje.id}>
                            <strong>De:</strong> {mensaje.emisor}<br />
                            <strong>Para:</strong> {mensaje.destinatario}<br />
                            <strong>Mensaje:</strong> {mensaje.contenido}<br />
                            <strong>Leído:</strong> {mensaje.leido ? 'Sí' : 'No'}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default Mensajes;
