import React, { useState, useEffect, useContext } from 'react';
import { Container, Row, Col, Button, ListGroup, ListGroupItem, Form, FormGroup, Label, Input, Alert } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { AuthContext } from '../components/AuthContext';

function ShoppingCart() {
    const { usuario } = useContext(AuthContext);
    const [items, setItems] = useState([]);
    const [direccionEnvio, setDireccionEnvio] = useState('');
    const [costoEnvio, setCostoEnvio] = useState(0);
    const [comuna, setComuna] = useState('');
    const [telefono, setTelefono] = useState('');
    const [email, setEmail] = useState('');
    const [nombre, setNombre] = useState('');
    const [isValid, setIsValid] = useState(true);

    useEffect(() => {
        if (usuario) {
            loadCartFromBackend();
        } else {
            loadCartFromLocalStorage();
        }
    }, [usuario]);

    const loadCartFromLocalStorage = () => {
        const storedCartItems = localStorage.getItem('cartItems');
        setItems(storedCartItems ? JSON.parse(storedCartItems) : []);
    };

    const loadCartFromBackend = async () => {
        try {
            const response = await axios.get('/api/cart', {
                headers: { Authorization: `Bearer ${usuario.token}` }
            });
            setItems(response.data);
        } catch (error) {
            console.error("Error al cargar el carrito desde el backend:", error);
        }
    };

    const formatPrice = (price) => `$${price.toLocaleString('es-CL')}`;
    const total = items.reduce((acc, item) => acc + (item.Precio * item.cantidad), 0);

    const clearCart = () => {
        localStorage.removeItem('cartItems');
        setItems([]);
    };

    const updateQuantity = (Nombre, change) => {
        const newItems = items.map(item => item.Nombre === Nombre ? { ...item, cantidad: Math.max(1, item.cantidad + change) } : item);
        setItems(newItems);
        if (!usuario) {
            localStorage.setItem('cartItems', JSON.stringify(newItems));
        }
    };

    const removeItem = (Nombre) => {
        const newItems = items.filter(item => item.Nombre !== Nombre);
        setItems(newItems);
        if (!usuario) {
            localStorage.setItem('cartItems', JSON.stringify(newItems));
        }
    };

    const procesarPago = async () => {
        const totalConEnvio = total + costoEnvio;
        const headers = usuario && usuario.token ? { Authorization: `Bearer ${usuario.token}` } : {};
        
        try {
            const pedidoData = {
                usuario_id: usuario ? usuario.id : null,
                total: totalConEnvio,
                metodo_pago: 'OnePay',
            };
            const pedidoRes = await axios.post('http://localhost:5000/api/pedidos', pedidoData, { headers });
    
            for (const item of items) {
                if (typeof item.productoVarianteId === 'undefined') {
                  alert(`El producto ${item.Nombre} no tiene un ID de variante definido.`);
                  return; // Detiene la ejecución si no hay ID de variante
                }
                const detallePedidoData = {
                  pedidoId: pedidoRes.data.pedidoId,
                  productoVarianteId: item.productoVarianteId,
                  cantidad: item.cantidad,
                  precio: item.Precio
                };
                await axios.post('http://localhost:5000/api/detallepedidos', detallePedidoData, { headers });
            }

            // Crear un pago simulado
            const pagoSimuladoData = {
                pedidoId: pedidoRes.data.pedidoId,
                monto: totalConEnvio,
                moneda: 'CLP',
                usuarioId: usuario ? usuario.id : null, // Si el usuario no está logueado, este valor será null
            };
            await axios.post('http://localhost:5000/api/crearPagoSimulado', pagoSimuladoData, { headers });

            // Si todo sale bien, mostrar un mensaje de éxito y limpiar el carrito
            alert('Pago realizado con éxito.');
            clearCart();
        } catch (error) {
            // Manejar los errores adecuadamente
            console.error('Error en el proceso de pago:', error);
            alert('Hubo un error al procesar el pago: ' + error.message);
        }
    };

    return (
        <Container>
            <Row>
                <Col md="8">
                    <h2>Carro ({items.length} productos)</h2>
                    <ListGroup>
                        {items.map((item, index) => (
                            <ListGroupItem key={index}>
                                <Row>
                                    <Col md="6">
                                        <strong>{item.Nombre}</strong> - <em>Talla: {item.talla}</em>
                                    </Col>
                                    <Col md="2">{formatPrice(item.Precio)}</Col>
                                    <Col md="4">
                                        <Button onClick={() => updateQuantity(item.Nombre, -1)}>-</Button>
                                        {` ${item.cantidad} `}
                                        <Button onClick={() => updateQuantity(item.Nombre, 1)}>+</Button>
                                        <Button color="warning" onClick={() => removeItem(item.Nombre)}>Eliminar</Button>
                                    </Col>
                                </Row>
                            </ListGroupItem>
                        ))}
                    </ListGroup>
                    <Button color="danger" block onClick={clearCart}>Vaciar Carrito</Button>
                </Col>
                <Col md="4">
                    <h2>Resumen de la compra</h2>
                    <ListGroup>
                        <ListGroupItem>
                            <Row>
                                <Col>Total</Col>
                                <Col>{formatPrice(total)}</Col>
                            </Row>
                        </ListGroupItem>
                        <ListGroupItem>
                            <h3>Información de Envío</h3>
                            <Form>
                                {/* Formulario de datos de envío */}
                                <FormGroup>
                                    <Label>Dirección de Envío</Label>
                                    <Input type="text" value={direccionEnvio} onChange={(e) => setDireccionEnvio(e.target.value)} />
                                </FormGroup>
                                {/* Agregar nuevos campos */}
                                <FormGroup>
                                    <Label>Comuna</Label>
                                    <Input type="text" value={comuna} onChange={(e) => setComuna(e.target.value)} />
                                </FormGroup>
                                <FormGroup>
                                    <Label>Teléfono</Label>
                                    <Input type="tel" value={telefono} onChange={(e) => setTelefono(e.target.value)} />
                                </FormGroup>
                                <FormGroup>
                                    <Label>Email</Label>
                                    <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                                </FormGroup>
                                <FormGroup>
                                    <Label>Nombre</Label>
                                    <Input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
                                </FormGroup>
                            </Form>
                            {!isValid && <Alert color="danger">Todos los campos son obligatorios para el envío.</Alert>}
                        </ListGroupItem>
                    </ListGroup>
                    <Button color="primary" block onClick={procesarPagoModificado}>Pagar</Button>
                </Col>
            </Row>
        </Container>
    );
}

export default ShoppingCart;
