//DetallesProducto.js
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Card, Button, Form } from 'react-bootstrap';
import { AuthContext } from '../components/AuthContext'

// Esta función se utiliza para obtener la URL completa de la imagen.
const getImageUrl = (imageName) => {
  return `http://localhost:5000/${imageName}`;
};

const DetallesProducto = ({ productoId }) => {
  const [producto, setProducto] = useState(null);
  const [reseñas, setReseñas] = useState([]);
  const [comentario, setComentario] = useState('');
  const [valoracion, setValoracion] = useState(0);
  const [variantes, setVariantes] = useState([]);
  const [tallaSeleccionada, setTallaSeleccionada] = useState('');
  const [cantidadAComprar, setCantidadAComprar] = useState(1);
  const [stockDisponible, setStockDisponible] = useState(0);

  const { usuario } = useContext(AuthContext);
  const estrellas = [1, 2, 3, 4, 5];

  const manejarValoracion = (valor) => {
    setValoracion(valor);
  };

  useEffect(() => {
    if (!productoId) {
      console.error('No se proporcionó ID de producto');
      return;
    }

    const fetchData = async () => {
      try {
        const resProducto = await axios.get(`http://localhost:5000/api/ObtenerProductoPorId/${productoId}`);
        console.log("Datos del producto recibidos:", resProducto.data);
        if (resProducto.data && resProducto.data.Imagen) {
          setProducto({
            ...resProducto.data,
            imageUrl: getImageUrl(resProducto.data.Imagen)
          });
        } else {
          console.error('Datos del producto no disponibles o sin imagen');
        }

        const resReseñas = await axios.get(`http://localhost:5000/api/resenas/${productoId}`);
        setReseñas(resReseñas.data);
      } catch (error) {
        console.error('Error al cargar el producto o las reseñas:', error);
      }
    };

    fetchData();
  }, [productoId]);


  const enviarComentario = () => {
    if (!usuario || !usuario.id) {
      alert("Solo los usuarios registrados o logueados pueden dejar comentarios.");
      return;
    }
    if (!comentario.trim()) {
      alert("Por favor, ingrese un comentario.");
      return;
    }
    if (valoracion < 1 || valoracion > 5) {
      alert("Por favor, ingrese una valoración válida.");
      return;
    }

    const comentarioData = {
      usuario_id: usuario.id,
      producto_id: producto.Id,
      valoracion,
      comentario
    };


    axios.post('http://localhost:5000/api/crearOpinionValoracion', comentarioData)
      .then(response => {
        alert("Comentario enviado con éxito.");
        setComentario('');
        setValoracion(0);
      })
      .catch(err => {
        console.error("Error al enviar el comentario:", err);
        alert("Hubo un error al enviar el comentario.");
      });
  };

  // Funciones nuevas para manejar la selección de talla y la cantidad a comprar
  const handleTallaChange = (event) => {
    const talla = event.target.value;
    setTallaSeleccionada(talla);
    const varianteSeleccionada = variantes.find(v => v.Talla === talla);
    setStockDisponible(varianteSeleccionada ? varianteSeleccionada.Cantidad : 0);
    setCantidadAComprar(1); // Reinicia la cantidad al cambiar de talla
  };

  const incrementarCantidadAComprar = () => {
    if (cantidadAComprar < stockDisponible) {
      setCantidadAComprar(cantidadAComprar + 1);
    }
  };

  const decrementarCantidadAComprar = () => {
    if (cantidadAComprar > 1) {
      setCantidadAComprar(cantidadAComprar - 1);
    }
  };

  if (!producto) {
    return <div>Cargando detalles del producto...</div>;
  }


  const tieneImagenes = Array.isArray(producto.imagenes) && producto.imagenes.length > 0;


  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          {/* Contenedor de la imagen con un ancho máximo y centrado */}
          <div style={{ maxWidth: '300px', margin: 'auto' }}>
            <img 
              src={producto.imageUrl} 
              alt={`Imagen de ${producto.Nombre}`} 
              style={{ width: '100%', height: 'auto' }} 
            />
          </div>
        </div>
        <div className="col-md-6">
          <div style={{ padding: '20px' }}>
            <h2>{producto.Nombre}</h2>
            {/* Componente para estrellas de valoración, si lo tienes */}
            <div className="my-2">
              {/* Estrellas de valoración aquí */}
            </div>
            <h3 style={{ color: 'green' }}>${producto.Precio}</h3>
            {/* Selector de colores, tallas y botones si los tienes */}
            <div className="my-2">
              {/* Opciones de color y talla aquí */}
            </div>
            <button style={{ backgroundColor: 'blue', color: 'white', padding: '10px 20px', border: 'none' }}>
              Añadir al Carrito
            </button>
          </div>
        </div>
      </div>
      {/* Sección de reseñas */}

      <h2 className="mb-3">Reseñas</h2>
      {reseñas.map(reseña => (
        <div key={reseña.id} className="mb-2">

        </div>
      ))}


      <Card className="mt-4 mb-4">
        <Card.Body>
          <h3>Dejar un Comentario</h3>
          <Form>
            <div className="mb-3">
              <Form.Label>Valoración</Form.Label>
              <div>
                {estrellas.map((num) => (
                  <span
                    key={num}
                    onClick={() => manejarValoracion(num)}
                    style={{ cursor: 'pointer', color: num <= valoracion ? 'orange' : 'grey' }}
                  >
                    ★
                  </span>
                ))}
              </div>
            </div>
            {/* Resto de tu formulario */}
            <Form.Group className="mb-3">
              <Form.Label>Comentario</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={comentario}
                onChange={(e) => setComentario(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" onClick={enviarComentario}>Enviar Comentario</Button>
          </Form>
        </Card.Body>
      </Card>
    </div>
  );
};

export default DetallesProducto;
