import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, ListGroup, ListGroupItem } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function ShoppingCart() {
    const [items, setItems] = useState([]);

    useEffect(() => {
        loadCartFromLocalStorage();
        // Agregar un escuchador para los cambios en localStorage
        const handleStorageChange = () => {
            loadCartFromLocalStorage();
        };

        window.addEventListener('storage', handleStorageChange);

        // Limpiar el escuchador
        return () => {
            window.removeEventListener('storage', handleStorageChange);
        };
    }, []);

    const loadCartFromLocalStorage = () => {
        const storedCartItems = localStorage.getItem('cartItems');
        const cartItems = storedCartItems ? JSON.parse(storedCartItems) : [];
        setItems(cartItems);
    };

    const formatPrice = (price) => `$${price.toLocaleString('es-CL')}`;

    const clearCart = () => {
        localStorage.removeItem('cartItems');
        setItems([]);
        alert('El carrito se ha vaciado correctamente.');
    };

    const saveCartToLocalStorage = (newItems) => {
        localStorage.setItem('cartItems', JSON.stringify(newItems));
        setItems(newItems); // Actualiza el estado para reflejar los cambios en la UI
    };

    const increaseQuantity = (Nombre) => {
        const newItems = items.map(item =>
            item.Nombre === Nombre ? { ...item, cantidad: item.cantidad + 1 } : item
        );
        saveCartToLocalStorage(newItems);
    };
    
    const decreaseQuantity = (Nombre) => {
        const newItems = items.map(item =>
            item.Nombre === Nombre ? { ...item, cantidad: Math.max(1, item.cantidad - 1) } : item
        );
        saveCartToLocalStorage(newItems);
    };
    
    

    const removeItem = (Nombre) => {
        const newItems = items.filter(item => item.Nombre !== Nombre);
        saveCartToLocalStorage(newItems);
    };

    const total = items.reduce((acc, item) => acc + (item.Precio * item.cantidad), 0);

      return (
    <Container>
      <Row className="my-5">
        <Col md="8">
          <h2 className="mb-4">Carro ({items.length} productos)</h2>
          <ListGroup>
            {items.map((item, index) => (
              <ListGroupItem key={index} className="mb-3 shadow">
                <Row>
                  <Col md="6" className="d-flex align-items-center">
                    <img src={item.imagen} alt={item.Nombre} style={{ width: '50px', height: '50px', marginRight: '10px' }} />
                    <strong>{item.Nombre}</strong>
                  </Col>
                  <Col md="2" className="d-flex align-items-center justify-content-center">
                    {formatPrice(item.Precio)}
                  </Col>
                  <Col md="4" className="d-flex align-items-center justify-content-end">
                    <Button size="sm" color="info" onClick={() => decreaseQuantity(item.Nombre)}>-</Button>
                    <span className="mx-2">{item.cantidad}</span>
                    <Button size="sm" color="info" onClick={() => increaseQuantity(item.Nombre)}>+</Button>
                    <Button size="sm" color="danger" onClick={() => removeItem(item.Nombre)} className="ml-2">Eliminar</Button>
                  </Col>
                </Row>
              </ListGroupItem>
            ))}
          </ListGroup>
        </Col>
        <Col md="4">
          <h2 className="mb-4">Resumen de la compra</h2>
          <ListGroup>
            <ListGroupItem className="shadow">
              <Row>
                <Col><strong>Total</strong></Col>
                <Col className="text-right">{formatPrice(total)}</Col>
              </Row>
            </ListGroupItem>
          </ListGroup>
          <Button color="danger" block onClick={clearCart} className="mt-3">Vaciar Carrito</Button>
          <Button color="success" block className="mt-3">Pagar</Button>
        </Col>
      </Row>
    </Container>
  );
}

export default ShoppingCart;
