// AdminPage.js
import React, { useState } from 'react';
//import CargaColegio from '../pages/CargaColegio';
import { Nav, NavItem, NavLink } from 'reactstrap';
import UpProducto from '../pages/UpProducto';
import 'bootstrap/dist/css/bootstrap.min.css';

function AdminPage() {
    const [currentView, setCurrentView] = useState('UpProducto');
    const changeView = (view) => {
        setCurrentView(view);
    };

    return (
        <div>
            <h1 className="my-4 text-center">Bienvenido administrador</h1>
            {/* Utilizar Nav de Reactstrap para una mejor apariencia */}
            <Nav tabs className="justify-content-center mb-4">
                <NavItem>
                    <NavLink
                    active={currentView ==='CargaColegio'}
                    onClick={() => setCurrentView('CargaColegio')}
                        style={{ cursor: 'pointer' }}
                    >
                        Cargar Colegios
                    </NavLink>
                    </NavItem>
                <NavItem>
                    <NavLink
                        active={currentView === 'Colegios'}
                        onClick={() => setCurrentView('Colegios')}
                        style={{ cursor: 'pointer' }}
                    >
                        Buscar por Colegio
                    </NavLink>
                </NavItem>
                <NavItem>
                    <NavLink
                        active={currentView === 'ShoppingCart'}
                        onClick={() => setCurrentView('ShoppingCart')}
                        style={{ cursor: 'pointer' }}
                    >
                        Carro
                    </NavLink>
                </NavItem>
            </Nav>

            {/* Renderizado condicional de componentes */}
            {currentView === 'A' && <Producto changeView={changeView} />}
            {currentView === 'Colegios' && <Colegios />}
            {currentView === 'ShoppingCart' && <ShoppingCart />}
        </div>
    );
}

export default AdminPage;
