import React, { useState } from 'react';
import { Nav, NavItem, NavLink } from 'reactstrap';
import { useAuth } from './AuthContext';
import { Navigate } from 'react-router-dom';
import Productos from '../pages/Productos';
import Colegios from '../pages/Colegios';
import ShoppingCart from '../pages/ShoppingCart';
import Home from './Home';
import Login from './Login';
import Registro from './Registro';
import Nosotros from '../pages/Nosotros';
import Soporte from '../pages/Soporte';
import 'bootstrap/dist/css/bootstrap.min.css';

function UserPage({ NombreUsuario }) {
    const { usuario } = useAuth();
    const [currentView, setCurrentView] = useState('Home');

    if (!usuario) {
        return <Navigate to="/" />;
    }

    const changeView = (view) => {
        setCurrentView(view);
    };

    // Estilos para el encabezado y la barra de navegación
    const headerStyle = {
        position: 'fixed',
        //top: 0,
        width: '100%',//ancho
        zIndex: 1000, 
        backgroundColor: '#FFF',
        padding: '10px 0' // Asegúrate de que el encabezado tenga un padding vertical para dar espacio al texto
    };

    const navStyle = {
        position: 'fixed',
        top: NombreUsuario ? '100px' : '60px', // Ajusta esto según la altura real de tu encabezado
        width: '100%',
        zIndex: 1020
    };

    // Estilo para el contenido principal para evitar que se solape con el encabezado fijo
    const mainContentStyle = {
        paddingTop: NombreUsuario ? '140px' : '100px', // Esto debe ser la suma de la altura del encabezado más la barra de navegación
    };

    return (
        <div>
            {/* Encabezado fijo */}
            <div style={headerStyle}>
                <img src="VIASMAE.png" alt="Logo VIASMAE" width="150" />
                {NombreUsuario && <div style={{ backgroundColor: '#ffdd99', textAlign: 'center' }}>Bienvenido, {NombreUsuario}!</div>}
                <h2 className="text-center">La Mayor Selección de Uniformes Escolares en Santiago</h2>
            

            {/* Barra de navegación fija */}
            <Nav tabs className="justify-content-center" style={navStyle}>
                <NavItem>
                    <NavLink
                        active={currentView === 'Home'}
                        onClick={() => setCurrentView('Home')}
                        style={{ cursor: 'pointer' }}
                    >
                        Home
                    </NavLink>
                </NavItem>
                <NavItem>
                    <NavLink
                        active={currentView === 'Soporte'}
                        onClick={() => setCurrentView('Soporte')}
                        style={{ cursor: 'pointer' }}
                    >
                        Contactanos
                    </NavLink>
                </NavItem>

                <NavItem>
                    <NavLink
                        active={currentView === 'Productos'}
                        onClick={() => setCurrentView('Productos')}
                        style={{ cursor: 'pointer' }}
                    >
                        Productos
                    </NavLink>
                </NavItem>
                <NavItem>
                    <NavLink
                        active={currentView === 'Colegios'}
                        onClick={() => setCurrentView('Colegios')}
                        style={{ cursor: 'pointer' }}
                    >
                        Buscar por Colegio
                    </NavLink>
                </NavItem>
                <NavItem>
                    <NavLink
                        active={currentView === 'ShoppingCart'}
                        onClick={() => setCurrentView('ShoppingCart')}
                        style={{ cursor: 'pointer' }}
                    >
                        Carro
                    </NavLink>
                </NavItem>
                <NavItem>
                    <NavLink
                        active={currentView === 'Login'}
                        onClick={() => setCurrentView('Login')}
                        style={{ cursor: 'pointer' }}
                    >
                        Mi cuenta
                    </NavLink>
                </NavItem>
            </Nav>
            </div>
       

             {/* Contenido principal */}
            <div style={mainContentStyle}>
                {/* Renderizado condicional de componentes */}
                {currentView === 'Home' && <Home changeView={changeView} />}
                {currentView === 'Productos' && <Productos changeView={changeView} />}
                {currentView === 'Colegios' && <Colegios />}
                {currentView === 'ShoppingCart' && <ShoppingCart />}
                {currentView === 'Login' && <Login changeView={changeView} />}
                {currentView === 'Registro' && <Registro changeView={changeView} />}
                {currentView === 'Nosotros' && <Nosotros />}
                {currentView === 'Soporte' && <Soporte />}
            </div>
        </div>
    );
}

export default UserPage;
