import React, { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext({
    usuario: null,
    login: () => {},
    logout: () => {},
});

export const AuthProvider = ({ children }) => {
    const [usuario, setUsuario] = useState(null);

    useEffect(() => {
        // Opcional: Recuperar el estado del usuario del almacenamiento local
        const usuarioAlmacenado = localStorage.getItem('usuario');
        if (usuarioAlmacenado) {
            setUsuario(JSON.parse(usuarioAlmacenado));
        }
    }, []);

    const login = (datosUsuario) => {
        localStorage.setItem('token', datosUsuario.token);
        const usuarioActualizado = {
            Id: datosUsuario.Id,
            TipoUsuario: datosUsuario.TipoUsuario,
            token: datosUsuario.token
        };
        setUsuario(usuarioActualizado);
        localStorage.setItem('usuario', JSON.stringify(usuarioActualizado)); // Opcional: Guardar el usuario en el almacenamiento local
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('usuario'); // Opcional: Borrar el usuario del almacenamiento local
        setUsuario(null);
    };

    return (
        <AuthContext.Provider value={{ usuario, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
