// frontend/src/components/UserPage.js
import React, { useState } from 'react';
import { Card, Button } from 'react-bootstrap';
import Footer from '../pages/Footer';

const productos = [
  { id: 1, nombre: 'Uniforme Escolar Niños', precio: '20.000' },
  { id: 2, nombre: 'Uniforme Escolar Niñas', precio: '22.000' },
  // Agrega más productos según sea necesario
];

const UserPage = () => {
  const [carrito, setCarrito] = useState([]);

  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, producto]);
  };

  return (
    <div className="container mt-5">
      <h2>Productos Disponibles</h2>
      <div className="row">
        {productos.map((producto) => (
          <div key={producto.id} className="col-md-4 mb-3">
            <Card>
              <Card.Body>
                <Card.Title>{producto.nombre}</Card.Title>
                <Card.Text>Precio: ${producto.precio}</Card.Text>
                <Button variant="primary" onClick={() => agregarAlCarrito(producto)}>
                  Agregar al Carrito
                </Button>
              </Card.Body>
            </Card>
          </div>
        ))}
      </div>
      {carrito.length > 0 && (
        <div className="mt-5">
          <h3>Carrito de Compras</h3>
          <ul>
            {carrito.map((item, index) => (
              <li key={index}>
                {item.nombre} - ${item.precio}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default UserPage;
