import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, ListGroup, ListGroupItem } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { AuthContext } from '../components';
import axios from 'axios';

function ShoppingCart() {
    const { usuario } = useContext(AuthContext);
    const [items, setItems] = useState([]);
    const [direccionEnvio, setDireccionEnvio] = useState('');
    const [costoEnvio, setCostoEnvio] = useState(0);
    const [usuarioId, setUsuarioId] = useState(1);

    useEffect(() => {
        setCostoEnvio(5000); // Costo de envío fijo para la demostración
    }, [direccionEnvio]);

    useEffect(() => {
        loadCartFromLocalStorage();
        // Simular el cálculo del costo de envío (podría ser más complejo en una implementación real)
        setCostoEnvio(5000); // Costo de envío fijo para la demostración
        window.addEventListener('storage', loadCartFromLocalStorage);
        return () => window.removeEventListener('storage', loadCartFromLocalStorage);
    }, []);

    useEffect(() => {
        // Verifica si el usuario está logueado
        if (!usuario) {
            // Si no está logueado, vacía el carrito
            clearCart();
        } else {
            // Si está logueado, carga el carrito desde localStorage
            loadCartFromLocalStorage();
        }
    }, [usuario]); // Añade 'usuario' como dependencia del useEffect
    

    const loadCartFromLocalStorage = () => {
        const storedCartItems = localStorage.getItem('cartItems');
        setItems(storedCartItems ? JSON.parse(storedCartItems) : []);
    };

    const formatPrice = (price) => `$${price.toLocaleString('es-CL')}`;
    const total = items.reduce((acc, item) => acc + (item.Precio * item.cantidad), 0);
    
    const clearCart = () => {
        localStorage.removeItem('cartItems');
        setItems([]);
        alert('El carrito se ha vaciado correctamente.');
    };

    const saveCartToLocalStorage = (newItems) => {
        localStorage.setItem('cartItems', JSON.stringify(newItems));
        setItems(newItems); // Actualiza el estado para reflejar los cambios en la UI
    };

     const increaseQuantity = (Nombre) => updateQuantity(Nombre, 1);
    const decreaseQuantity = (Nombre) => updateQuantity(Nombre, -1);

    const updateQuantity = (Nombre, change) => {
        const newItems = items.map(item => {
            if (item.Nombre === Nombre) {
                return {...item, cantidad: Math.max(1, item.cantidad + change)};
            }
            return item;
        });
        setItems(newItems);
        localStorage.setItem('cartItems', JSON.stringify(newItems));
    };

    const removeItem = (Nombre) => {
        const newItems = items.filter(item => item.Nombre !== Nombre);
        setItems(newItems);
        localStorage.setItem('cartItems', JSON.stringify(newItems));
    };

    const procesarPago = async () => {
        const totalConEnvio = total + costoEnvio;
        try {
            const pedidoRes = await axios.post('/api/crearPedido', {
                usuario_id: usuarioId,
                total: totalConEnvio,
                metodo_pago: 'OnePay'
            });

            for (const item of items) {
                await axios.post('/api/agregarDetallePedido', {
                    pedido_id: pedidoRes.data.pedidoId,
                    productoVariante_id: item.id,
                    cantidad: item.cantidad,
                    precio: item.Precio
                });
            }

            await axios.post('/api/crearPagoSimulado', {
                pedidoId: pedidoRes.data.pedidoId,
                monto: totalConEnvio,
                moneda: 'CLP',
                usuarioId: usuarioId
            });

            alert('Pago realizado con éxito.');
            clearCart();
        } catch (error) {
            console.error('Error en el proceso de pago:', error);
            alert('Hubo un error al procesar el pago.');
        }
    };


    return (
        <Container>
            <Row>
                <Col md="8">
                    <h2>Carro ({items.length} productos)</h2>
                    <ListGroup>
                        {items.map(item => (
                            <ListGroupItem key={`${item.Nombre}-${item.talla}`}>
                                <Row>
                                    <Col md="6">
                                        <strong>{item.Nombre}</strong> - <em>Talla: {item.talla}</em>
                                    </Col>
                                    <Col md="2">
                                        {formatPrice(item.Precio)}
                                    </Col>
                                    <Col md="2">
                                        <Button onClick={() => decreaseQuantity(item.Nombre)}>-</Button>
                                        {` ${item.cantidad} `}
                                        <Button onClick={() => increaseQuantity(item.Nombre)}>+</Button>
                                    </Col>
                                    <Col md="2">
                                        <Button color="warning" onClick={() => removeItem(item.Nombre)}>Eliminar</Button>
                                    </Col>
                                </Row>
                            </ListGroupItem>
                        ))}
                    </ListGroup>
                    <Button color="danger" block onClick={clearCart}>Vaciar Carrito</Button>
                </Col>
                <Col md="4">
                    <h2>Resumen de la compra</h2>
                    <ListGroup>
                        <ListGroupItem>
                            <Row>
                                <Col>Total</Col>
                                <Col>{formatPrice(total)}</Col>
                            </Row>
                        </ListGroupItem>
                        <ListGroupItem>
                            <h3>Información de Envío</h3>
                            <input
                                type="text"
                                placeholder="Dirección de Envío"
                                value={direccionEnvio}
                                onChange={(e) => setDireccionEnvio(e.target.value)}
                            />
                            <p>Costo de envío: {formatPrice(costoEnvio)}</p>
                        </ListGroupItem>
                    </ListGroup>
                    <Button color="primary" block onClick={procesarPago}>Pagar</Button>
                </Col>
            </Row>
        </Container>
    );
}

export default ShoppingCart;
